import { REMOVE_FAVORITE, ADD_FAVORITE, FILTER, ORDER } from "./actions";

const initialState = {
  myFavorites: [],
  allCharacters: []
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAVORITE:
      return { ...state, allCharacters: [...state.allCharacters, action.payload],myFavorites:[...state.allCharacters, action.payload] };
    case REMOVE_FAVORITE:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          (char) => char.id !== action.payload
        ),
      };
     case FILTER:
      const filterByGender =[...state.allCharacters].filter((char)=>char.gender === payload)
      return {
        ...state,
        myFavorites: filterByGender
      };
      case ORDER:
        const filterByOrder = [...state.allCharacters].sort((a,b)=>{
          if(a.id > b.id){
            return payload === "Ascendente" ? 1 : -1
          }else if (a.id < b.id){
            return payload === "Descendente" ? 1 : -1
          }
        })

    default:
      return state; 
  }
};



export default rootReducer;
