import style from "./Card.module.css";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { addFavorite, removeFavorite } from "../../redux/actions";
import { useState } from "react";
import { useEffect } from "react";

 function Card({ id, name , species , gender , image , onClose , addFavorite ,removeFavorite,myFavorites}) {

const [isFav, setIsFav]= useState(false);



const handleFavorite = () => {
   if(isFav) {
      setIsFav(false);
      removeFavorite(id);
   }else{
      setIsFav(true);
      addFavorite({
         id, name , species , gender , image , onClose , addFavorite ,removeFavorite,
      });
   }
}

useEffect(() => {
   myFavorites.forEach((fav) => {
      if(fav.id === id) {
         setIsFav(true);
      }
   });
}, [myFavorites]);


    return (
       <div className={style.container}>
         {isFav ? (
            <button onClick={handleFavorite}>üíì</button>
         ) : (
            <button onClick={handleFavorite}>‚ù§</button>
         )}
          <button onClick={() => onClose(id)} className={style.closeButton}>X</button>
          <Link to={`/detail/${id}`}>
          <h2>Name: {name}</h2>
          </Link>
          <h2>Species: {species}</h2>
          <h2>Gender: {gender}</h2>
          <img src={image} alt="" /> 
    </div>
 );
 }

const mapDispathToProps = (dispatch) => {
return {
   addFavorite: (character) => {
      dispatch(addFavorite(character));
   }, 
   removeFavorite: (id)=> {
      dispatch(removeFavorite(id));
   },
};
};

const mapStatetoProps = (state) => {
   return {
      myFavorites: state.myFavorites,
      
   };

}


 export default connect(mapStatetoProps,mapDispathToProps)(Card);
